          colm = readlineSync.question('Inserte el numero de la columna del 1-7 = ');
          // console.log(colm);
        }
      }
      result = this.aJugar(colm, this.jugadores[turno].ficha, print);
      if (result) {
        return `Gana el jugador ${turno + 1}`;
      }
      // this.tablero.printTablero();
